import React, {useEffect, useState} from 'react'

const NextRace = ({socket}) => {
    const [session, setSession] = useState(null)
    const [alert] = useState("")
    const [lastCallMessage, setLastCallMessage] = useState(false)
    useEffect(() => {
        socket.emit("get_next_session")
    }, [])

    useEffect(() => {
        socket.on("next_session", (data) => setSession(data))
        socket.on("last_call_message", (data) => setLastCallMessage(data))
        return () => {
            socket.off("next_session")
            socket.off("last_call_message")
        }
    }, [socket]);
    return (
        <div className="fullscreenDiv">
            {alert && <div className="error">{alert}</div>} {/* Display error messages */}
            {lastCallMessage &&
                <div className="error">Proceed to the paddock</div>} {/* Display call to get ready messages */}
            {session ? (
                <div>
                    <h2>Next Starting Session</h2>
                    <p>{session.id}</p>
                    <p>{session.name}</p>
                    <p>Starts at: {new Date(session.startTime).toLocaleString()}</p>
                    <p>Duration: {session.duration} minutes</p>
                    <ul>
                        {session.drivers && session.drivers.length > 0 ? (
                            session.drivers.map((driver, index) => (
                                <li key={index}>
                                    Driver: {driver.name} Car: {driver.carNumber}
                                </li>
                            ))) : (
                            <li>No drives available. </li>
                        )}
                    </ul>
                </div>
            ) : (
                <p>No Races</p>
            )}
        </div>
    )
}

export default NextRace
