import {useEffect, useState} from "react";
import RaceFlags from "./RaceFlags";

function RaceControl({socket}) {
    const [raceMode, setRaceMode] = useState("Safe");
    const [remainingTime, setRemainingTime] = useState(0);
    const [isRaceRunning, setIsRaceRunning] = useState(false);

    useEffect(() => {
        socket.on("race_state", (data) => {
            setRaceMode(data.raceMode);
            setRemainingTime(data.remainingTime)
            setIsRaceRunning(data.isRaceRunning)
        });

        return () => {
            socket.off("race_state");
        }
    }, [socket]);

    const startRace = () => {
        socket.emit("start_race");
    }

    const stopRace = () => {
        socket.emit("stop_race");
    }

    const changeRaceMode = (mode) => {
        socket.emit("set_race_mode", mode);
    }

    const formatTime = (time) => {
        const minutes = Math.floor(time / 60).toString().padStart(2, "0");
        const seconds = (time % 60).toString().padStart(2, "0");

        return `${minutes}:${seconds}`;
    }

    return (
        <div className="race-control">
            <h1>Race Control</h1>
            <h2>Race Mode: {raceMode}</h2>
            <h2>Time Remaining: {formatTime(remainingTime)}</h2>
            <div>
                <button onClick={() => changeRaceMode("Safe")} disabled={!isRaceRunning}>Safe</button>
                <button onClick={() => changeRaceMode("Hazard")} disabled={!isRaceRunning}>Hazard</button>
                <button onClick={() => changeRaceMode("Danger")} disabled={!isRaceRunning}>Danger</button>
                <button onClick={() => changeRaceMode("Finish")} disabled={!isRaceRunning}>Finish</button>
            </div>
            <br/>
            <RaceFlags socket={socket}/>
            <br/>
            <div>
                <button onClick={startRace} disabled={isRaceRunning}>Start Race</button>
                <button onClick={stopRace} disabled={!isRaceRunning}>Stop Race</button>
            </div>
        </div>
    )
}

export default RaceControl;
