import {useEffect} from "react";
import {useSession} from "./components/SessionContext";

function MainPage({socket}) {
    const [sessions, setSessions] = useSession()

    useEffect(() => {
        socket.emit("get_all_sessions")
    },)

    useEffect(() => {
        socket.on("all_sessions", (data) => {
            if (Array.isArray(data)) {
                setSessions(data);
            } else {
                console.error("Received data is not an array:", data);
            }
        })
    }, [socket])
    return <div>
        <h2>Races List</h2>
        <ul>
            {/*TODO: sessions.map is not a function
MainPage */}
            {sessions && sessions.map((race, index) => (
                <li id={index}>
                    <strong>Start Time:</strong> {new Date(race.startTime).toLocaleString()} |
                    <strong>Started:</strong> {race.hasStarted.toString()} |
                    <strong>Finished:</strong> {race.hasFinished.toString()} |

                    <strong>Drivers:</strong> {race.drivers.map(driver => `${driver.name} (Car: ${driver.carNumber} Laps: ${driver.laps} Time: ${driver.fastestLap})`).join(', ')}

                </li>
            ))}
        </ul>
    </div>
}

export default MainPage
