import { useEffect, useState } from "react";

function RaceFlags({ socket }) {
    const [raceMode, setRaceMode] = useState("Safe");

    useEffect(() => {
        socket.on("race_state", (data) => {
            setRaceMode(data.raceMode);
        });

        return () => {
            socket.off("race_state");
        };
    }, [socket]);

    const getBackgroundColor = () => {
        switch (raceMode) {
            case "Safe":
                return "green";
            case "Hazard":
                return "yellow";
            case "Danger":
                return "red";
            case "Finish":
                return "linear-gradient(45deg, black 50%, white 50%)";
            default:
                return "white";
        }
    };

    return (
        <div
            style={{
                backgroundColor: raceMode === "Finish" ? undefined : getBackgroundColor(),
                backgroundImage: raceMode === "Finish" ? getBackgroundColor() : undefined,
                height: "300px",
                width: "100%",
                textAlign: "center",
                border: "2px solid black",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
            }}
        >
            {/* Span для текста с заблюренным фоном */}
            <span
                style={{
                    background: "rgba(255, 255, 255, 0.7)", // Полупрозрачный белый фон
                    backdropFilter: "blur(5px)", // Эффект размытия
                    padding: "10px 20px",
                    border: "1px solid black",
                    borderRadius: "10px",
                    fontSize: "2rem",
                    color: "black",
                    fontWeight: "bold",
                }}
            >
                Current Flag: {raceMode}
            </span>
        </div>
    );
}

export default RaceFlags;
