import React, { useState, useEffect } from "react";

function LapLineTracker({ socket }) {
    const [cars, setCars] = useState([]);

    useEffect(() => {
        // Получение данных о машинах
        socket.on("init-cars", (initialCars) => {
            console.log("Received cars:", initialCars);
            setCars(initialCars);
        });

        socket.on("update-car", (updatedCar) => {
            setCars((prevCars) =>
                prevCars.map((car) =>
                    car.number === updatedCar.number
                        ? { ...car, fastestLap: updatedCar.fastestLap }
                        : car
                )
            );
        });

        return () => {
            socket.off("init-cars");
            socket.off("update-car");
        };
    }, [socket]);

    // Обработка события lap-cross
    const handleLapCross = (carNumber) => {
        const timestamp = Date.now();
        console.log("Emitting lap-cross event:", { carNumber, timestamp });
        socket.emit('lap-cross', { carNumber, timestamp });
    };

    return (
        <div>
            <h1>Lap Line Tracker</h1>
            {cars.length === 0 ? (
                <p>No cars available</p>
            ) : (
                <ul>
                    {cars.map((car) => (
                        <li key={car.number}>
                            <div style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
                                <span style={{ marginRight: '10px' }}>Car #{car.number}</span>
                                <button
                                    onClick={() => handleLapCross(car.number)}
                                    style={{
                                        padding: '10px 20px',
                                        backgroundColor: '#4caf50',
                                        color: 'white',
                                        border: 'none',
                                        borderRadius: '5px',
                                        cursor: 'pointer',
                                    }}
                                >
                                    Lap Cross
                                </button>
                                <span style={{ marginLeft: '10px' }}>
                                    Fastest Lap: {car.fastestLap ? `${car.fastestLap} ms` : 'N/A'}
                                </span>
                            </div>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default LapLineTracker;
