import React, {useEffect, useState} from "react";
import LapButtons from "./components/Button/LapButtons";

function LapLineTracker({socket}) {
    const [cars, setCars] = useState([]); //List of cars
    const [hideButtons, setHideButtons] = useState(false)
    //once on startup
    useEffect(() => {
        socket.emit('request-init-cars');
    }, []);

    useEffect(() => {
        //get initial list of cars
        socket.on('init-cars', (cars) => {
            setCars(cars);
        });

        //Update cars data
        socket.on('update-car', (updatedCar) => {
            setCars((prevCars) =>
                prevCars.map((car) =>
                    car.number === updatedCar.number
                        ? {...car, fastestLap: updatedCar.fastestLap, currentLap: updatedCar.currentLap}
                        : car
                )
            );
        });

        socket.on('last_call_message', (data) => {
            setHideButtons(data)
        })

        return () => {
            socket.off('init-cars');
            socket.off('update-car');
            socket.off('last_call_message')

        };
    }, [socket]);

    //Button handler
    const handleLapCross = (carNumber) => {
        const timestamp = Date.now();
        setCars((prevCars) =>
            prevCars.map((car) =>
                car.number === carNumber
                    ? {...car, currentLap: (car.currentLap || 0) + 1}
                    : car
            )
        );
        socket.emit('lap-cross', {carNumber, timestamp});
    };

    return (
        <div>
            <h1>Lap Line Tracker</h1>
            {hideButtons && <div>Race over</div>}
            {!hideButtons && <>
                {cars.length <= 0 ? (
                    <p>No cars available</p>
                ) : (
                    <ul>
                        {cars.map((car) => (
                            <li key={car.number}>
                                <div style={{display: 'flex', alignItems: 'center', marginBottom: '10px'}}>
                                    <span style={{marginRight: '10px'}}>Car #{car.number} ({car.name})</span>
                                    <LapButtons car={car}
                                                onClick={() => handleLapCross(car.number)}
                                                disabled={hideButtons}
                                    />
                                    <span style={{marginLeft: '10px'}}>
                      {`Lap# ${car.currentLap}`}
                    </span>
                                    <span style={{marginLeft: '10px'}}>
                      Fastest Lap: {car.fastestLap ? `${car.fastestLap} ms` : 'N/A'}
                    </span>
                                </div>
                            </li>
                        ))}
                    </ul>
                )}</>}
        </div>
    );

}

export default LapLineTracker;
